import * as React from 'react';
import { useEffect, useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Switch from '@mui/material/Switch';
import { Button } from '@mui/material';
import Typography from "@mui/material/Typography";
import { NavLink } from 'react-router-dom';
import Tooltip from '@mui/material/Tooltip';
import IconButton from '@mui/material/IconButton';
import Avatar from '@mui/material/Avatar';
import SvgIcon from '@mui/material/SvgIcon';
import { useTheme } from '@mui/system';

var isDarkMode = null;

function HomeIcon(props) {
  return (
    <SvgIcon {...props}>
      <path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z" />
    </SvgIcon>
  );
}


function Navigation({handleChange}) {

  console.log(`Local Storage: ${localStorage.getItem("DARK_MODE")}`);
  if (localStorage.getItem('DARK_MODE') === 'light'){
    isDarkMode = false;
  }
  else{
    isDarkMode = true;
  }
  console.log(`Is in dark mode? ${isDarkMode}`);
  const theme = useTheme();
  const [toggle, setToggle] = useState(false);
  useEffect(() => {
    // check when the component is loaded
    const localStorageToggled = localStorage.getItem("toggled");

    // If is not null
    if (localStorageToggled) {
      setToggle(localStorageToggled === "true" ? true : false);
    } else {
      // If null set the localStorage key/value as a string.
      localStorage.setItem("toggle", `${toggle}`);
    }
  }, []);

  function handleToggle() {
    localStorage.setItem("toggle", `${toggle}`);
    setToggle(toggle);
  };

  return (
    <AppBar >
      <Toolbar>
      <Tooltip title="Home">
        <IconButton href='/' color='inherit' >
          <HomeIcon />
        </IconButton>
      </Tooltip>
        <Typography variant="h6" component="div" href='/' sx={{ flexGrow: 1 }}></Typography>
          <Button variant='outline' href='/about-me'>About Me</Button>
          <Button variant='outline'>Resume</Button>
          <Switch checked={isDarkMode} onChange={handleChange} />
        {/*  <Tooltip title="Open settings">
             add: onClick={''} here 
              <IconButton  sx={{ p: 0 }}>
                <Avatar alt="Remy Sharp" src="/static/images/avatar/2.jpg" />
              </IconButton>
            </Tooltip>*/}
      </Toolbar>
    </AppBar>
  );

};

export default Navigation
