import React from "react";
import { ThemeProvider, createTheme } from "@mui/material";
import { CustomPalette } from "./theme/CustomPalette";
import Switch from "@mui/material/Switch";
import { useTheme } from "@mui/material";
import { Container } from "@mui/system";
const ColorModeContext = React.createContext({ toggleColorMode: () => {} });

function MyApp() {
    const storedDarkMode = localStorage.getItem("DARK_MODE");

    const theme = useTheme();
    const colorMode = React.useContext(ColorModeContext);


  useEffect(() => {
    localStorage.setItem("DARK_MODE", mode);
  }, [mode]);


  return (

    <Container sx={{bgcolor: 'primary.main'}}>
        <Switch onClick={colorMode.toggleColorMode}></Switch>
      <h1>Hello, world!       {theme.palette.mode} mode
        </h1>
      <h2>Let's create a dark mode toggle that persists when the page is reloaded.</h2>
      </Container>
  );
}

export default function ToggleColorMode() {
    const [mode, setMode] = React.useState('light');
    const colorMode = React.useMemo(
      () => ({
        toggleColorMode: () => {
          setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
        },
      }),
      [],
    );

    const theme = React.useMemo(
      () =>
        createTheme(CustomPalette(mode)),
      [mode],
    )
    return (
        <ColorModeContext.Provider value={colorMode}>
          <ThemeProvider theme={theme}>
            <MyApp />
          </ThemeProvider>
        </ColorModeContext.Provider>
      );
    };
