import React from "react";
import { ThemeProvider, createTheme } from "@mui/material";
import { CustomPalette } from "./theme/CustomPalette";
import Switch from "@mui/material/Switch";

function App() {
    const [mode, setMode] = React.useState('light');

    const [darkMode, setDarkMode] = React.useState(false);
    const toggleDarkMode = () => setDarkMode(darkMode ? false : true);

    const theme = createTheme(CustomPalette('dark'));
    if (darkMode === "dark"){
        let theme = createTheme(CustomPalette('dark'));
    }
    else{
        let theme = createTheme(CustomPalette('light'));

    }

  return (
    <ThemeProvider theme={theme}>

    <div className="App" data-theme={darkMode ? "dark" : "light"}>
        <Switch onClick={toggleDarkMode}></Switch>
      <h1>Hello, world!</h1>
      <h2>Let's create a dark mode toggle that persists when the page is reloaded.</h2>
    </div>

    </ThemeProvider>
  );
}

export default App;
